import { Component } from "react";
import logo from "./logo.svg";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      monsters: [
        { name: "Virendra", id: 1 },
        { name: "Aniketh", id: 2 },
        { name: "vishal", id: 3 },
      ],
    };
  }
  componentDidMount()
  {
    fetch().then()
  }

  render() {
    return (
      <div className="App">
        {this.state.monsters.map((monster) => {
          return (
            <div key={monster.id}>
              <h1>{monster.name}</h1>
            </div>
          );
        })}
      </div>
    );
  }
}

export default App;
