import { Component } from "react";
import logo from "./logo.svg";
import "./App.css";

class App extends Component {
  constructor() {
    console.log("constructor");
    super();
    this.state = {
      monsters: [],
      SearchField: "",
    };
  }

  componentDidMount() {
    console.log("mount");
    fetch("https://jsonplaceholder.typicode.com/users")
      .then((response) => response.json())
      .then((users) =>
        this.setState(
          () => {
            return { monsters: users };
          },
          () => {
            console.log(this.state);
          }
        )
      );
  }

  render() {
    console.log("render");
    const fmonster = this.state.monsters.filter((monster) => {
      return monster.name.toLowerCase().includes(this.state.SearchField);
    });
    return (
      <div className="App">
        <input
          type="search"
          placeholder="Monster's Name"
          className="moster-search"
          onChange={(event) => {
            const SearchField = event.target.value.toLowerCase();
            // console.log(fname);

            this.setState(() => {
              return { SearchField };
            });
          }}
        />

        {fmonster.map((monster) => {
          return (
            <div key={monster.id}>
              <h1>{monster.name}</h1>
            </div>
          );
        })}
      </div>
    );
  }
}

export default App;
